#! /usr/bin/env python3

# Write script that determines unique characters in DNA sequence
DNA_sequence = ""

DNA_sequence_uniq = set(DNA_sequence)
nt_count = {}

print(f'Unique characters in sequence are: {DNA_sequence_uniq}')

# Iterate over each character and count the number found in the sequence
# store each count in a dictionary
# when you are done counting calculate and report the nucleotide composition and the GC content

for nt in DNA_sequence_uniq:
	count = DNA_sequence.count(nt)
	nt_count[nt] = count

print (f'Nucleotide count: {nt_count}')
GC_Content = nt_count["G"] + nt_count["C"]
print(f'GC content: {GC_Content/len(DNA_sequence)}')
